package uz.gita.dictionaryimport android.content.Intentimport android.net.Uriimport android.os.Bundleimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport androidx.fragment.app.Fragmentimport uz.gita.dictionary.databinding.AppinfoScreenBindingclass AppInfoScreen : Fragment(R.layout.appinfo_screen) {    private lateinit var binding: AppinfoScreenBinding    override fun onCreateView(        inflater: LayoutInflater,        container: ViewGroup?,        savedInstanceState: Bundle?,    ): View {        binding = AppinfoScreenBinding.inflate(inflater, container, false)        return binding.root    }    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {        super.onViewCreated(view, savedInstanceState)        binding.myTg.setOnClickListener {            openTelegramProfile("azizbeklive")        }        binding.gitaInsta.setOnClickListener {            val intent =                Intent(Intent.ACTION_VIEW, Uri.parse("https://www.instagram.com/gita.uzofficial"))            startActivity(intent)        }        binding.gitaTg.setOnClickListener {            val intent = Intent(Intent.ACTION_VIEW, Uri.parse("https://t.me/gitauz"))            startActivity(intent)        }        binding.gitaYoutube.setOnClickListener {            val intent = Intent(                Intent.ACTION_VIEW,                Uri.parse("https://www.youtube.com/channel/UCjegZuzSYR4Ew5NDirQk_mg")            )            startActivity(intent)        }    }    private fun openTelegramProfile(telegramUserName: String) {        val intent = Intent(Intent.ACTION_VIEW, Uri.parse("https://t.me/$telegramUserName"))        startActivity(intent)    }}